ents' => $func->input_fun($_POST['fldOppComments'])									);									$db->insertRec('tbl_Opp_Comments',$strDataArr);									if(count($error_msg)==0)									{								?>								<div class="thankyoumessage">									<?php  echo "Your comment has been successfully posted.";?>								</div>								<?                                    }                                    }                                    }								?>								<?php									if (count($error_msg)>0)									{									foreach($error_msg as $key=>$value)									{								?>								<div class="thankyoumessage">									<?php  echo $value;?>								</div>								<?                                    }                                    }                                    if($error_msg!=""){                                    $fldOppComments       = $_REQUEST['fldOppComments'];                                    }								?>								<?php									$selquery = " Select * from ".TBL_ATHELETE_STAT. " where (fldPrograme ='".$_REQUEST['programId']."')									and (fldAtheleteId='".$_REQUEST['athid']."') and (fldCoachId!='".$_SESSION['Coach_id']."') and  (fldStatus=1)";									$db->query($selquery);									$db->next_record();									for($i=0;$i<$db->num_rows();$i++)									{									$fldLabelname=$db->f('fldLabelname');									$fldvalue=$db->f('fldValue');								?>								<p>									<label><?php  echo $fldLabelname;?>:</label>									<span><?php  echo $fldvalue;?></span>								</p>								<?php                                    $db -> next_record();                                    }								?>								<form action="" method="post">									<p>										<label>Comments:</label>										<span> 																					<textarea name="fldOppComments" id="fldOppComments" class="ta1"><?=$fldComments?></textarea>										</span>									</p>									<p>										<label>&nbsp;</label>										<span>											<input type="hidden" name="isSubmit" value="save">											<button type="submit" class="normalbtn">												Submit											</button>&nbsp;&nbsp;&nbsp;&nbsp;											<button TYPE="BUTTON" ONCLICK="history.go(-1)" class="normalbtn"></span>												Close											</button>									</p>								</form>							</div>						</div>					</div>				</div>			</div>		</div>	</body></html>