e {                    return true;                }            }		</script>	</head>	<body>		<?php        include ('header.php'); ?> <!--header link starts from here -->		<!--Header ends from here -->		<!--middle panel starts from here -->		<!--content panel starts from here -->		<div class="container">			<div class="innerWraper">				<div class="middle-bg">					<div class="cantener">						<div class="register-main">							<div class="registerPage smaller">								<form name="frmAthReg" action="" method="post" enctype="multipart/form-data" onsubmit="return validate()">									<h1>Athlete Stats</h1>																		<?php                                        if ($_REQUEST['msg']) {                                            echo '<div class="thankyoumessage">You have successfully added game stats, they will display on your profile after your Coach approves it.</div>';                                        }									?>																		<?php									$programe_valid=$func->selectTableOrder(TBL_ATHELETE_STAT,"fldPrograme,fldAtheleteId,fldSportid","fldPrograme","where fldPrograme=".$_GET['fldEventId']);									$programe_valid[0]['fldPrograme'];									$programe_valid[0]['fldAtheleteId'];									$programe_valid[0]['fldSportid'];									if(($userid==$programe_valid[0]['fldAtheleteId']) and ($_GET['fldEventId']==$programe_valid[0]['fldPrograme']) and($fldSport==$programe_valid[0]['fldSportid']))									{									if(!$_REQUEST['msg'])									{									echo '									<br>									<br>									<div class="thankyoumessage">										You have already added stats for this event.									</div>									<br>									';									echo '									<p>										<label>&nbsp;</label>										<span>											<INPUT TYPE="BUTTON" VALUE="Back" ONCLICK="history.go(-1)">										</span>									</p>									';									}									}									else {									?>									<p>										<label style="width:240px;font-size:15px;">Game / 