utput_fun($db -> f('fldFirstName'));                        $username = $func -> output_fun($db -> f('fldUserName'));                        $password = $func -> output_fun($db -> f('fldPassword'));                        $subjectStre = "College Prospect Network - Login Information";                        $bodyStre = "Hello&nbsp;" . ucfirst($firstname) . ",<br /><br />";                        $bodyStre .= "Here is your log-in information for College Prospect Network:<br /><br />";                        $bodyStre .= "Type: College Coach<br /><br />";                               $bodyStre .= "Username: " . $username . "<br />";                        $bodyStre .= "Password: " . $password . "<br /><br />";                        $bodyStre .= "Thank you," . "<br />";                        $bodyStre .= "College Prospect Network Support";                        $adminmail = ADMIN_EMAIL;                        $func -> sendEmail($fldEmail, $subjectStre, $bodyStre, $adminmail);                                            $db->next_record();                                          }                    header("Location: login.php?&msgforpass=Your password has been sent to your email from notifications@collegeprospectnetwork.com");                }            }        }        //ERROR - Go Back a Step        if ($error_msg != "") {            $step = 2;            //$fldEmail = $_REQUEST['fldEmail'];            //$question = $_REQUEST['question'];            $answer = "";            $attempts++;            if ($attempts > 4) {                $step = 1;                $attempts = 0;                $error_msg = 'Invalid Answer, Please retry.  If you still have problems, please contact us through our Contact page. <br /><br />Too many frequent Bad Attempts, please make sure CAPS Lock is turned off.';                $fldEmail = "";            }        }    }    ######################## END STEP3 - get Question + Answer #####################