<?php    ##  Project		:		Sport Social Networking - Admin Panel    ##  Done by		:		Narendra Singh    ##	Page name	:		Coach changePassword.php    ##	Create Date	:		17/07/2011    ##  Description :		It is use to send,recieve,delete the  Messages.    ##	Copyright   :       Synapse Communications Private Limited.    ## *****************************************************************    include_once ("inc/common_functions.php");    //for common function    include_once ("inc/page.inc.php");    session_start();    if (($_SESSION['mode'] == "") or ($_SESSION['FRONTEND_USER'] == "")) {        header("Location:index.php");    }    //for paging    $func = new COMMONFUNC;    $db = new DB;    $flag = 0;    $error_msg = '';    if ($_POST['isSubmit'] == 'save') {        if ($_SESSION['FRONTEND_USER'] != "") {            $whereClause = "fldUsername='" . $_SESSION['FRONTEND_USER'] . "' and fldPassword ='" . $func -> input_fun($_POST['oldpassword']) . "'";            if ($db -> MatchingRec(TBL_HS_AAU_COACH, $whereClause) == 0) {                $error_msg = 'Old Password incorrect, please re-try';            }            else {                if ($error_msg == '') {                    $strDataArr = array('fldPassword ' => $func -> input_fun($_POST['newpassword']));                    $where = "fldUsername='" . $_SESSION['FRONTEND_USER'] . "'";                    $db -> updateRec(TBL_HS_AAU_COACH, $strDataArr, $where);                    $$_SESSION['FRONTEND_USER'] = "";                    header("Location: Coachchangepassword.php&msg=Password successfully updated.");                }            }        }        //this section is use to filup the value after erro message.        if ($error_msg != "") {            $oldpassword = $_REQUEST['oldpassword'];            $newpassword = $_REQUEST['newpassword'];            $confirmnewpassword = $_REQUEST['confirmnewpassword'];        }    }?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional/