rue;                }            }		</script>	</head>	<body>		<?php            include ('header.php');		?>		<!--middle panel starts from here -->		<!--content panel starts from here -->		<div class="container">			<div class="innerWraper">				<div class="middle-bg">					<div class="cantener">						<div class="register-main">							<h1>Change Password</h1>							<div class="registerPage">								<form name="frmCoachReg" action="" method="post" enctype="multipart/form-data" onsubmit="return validate()">								        							 <?php                                    if ($error_msg!=''){                                    ?>                                    <div class="errormessage"><?php  echo $error_msg;?></div>                                    <?                                        }                                        if ($_REQUEST['msg']){                                    ?>                                        <div class="thankyoumessage"> <?php  echo $_REQUEST['msg'];?></font></div>                                                                  <?                                        }                                    ?>                                									<p>										<label>Old Password:</label>										<span>											<input type="password" name="oldpassword" value="<?=$oldpassword?>" />										</span><font color="#0000ff">*</font>									</p>									<p>										<label>New Password:</label>										<span>											<input type="password" name="newpassword" value="<?=$newpassword?>" />										</span><font color="#0000ff">*</font>									</p>									<p>										<label>Confirm Password:</label>										<span>											<input type="password" name="confirmnewpassword" value="<?=$confirmnewpassword?>" />										</span><font color="#0000ff">*</font>									</p>									<p>										<label>&nbsp;</label>										<span>											<input type="hidden" name="isSubmit" value="