<?php    include_once ("inc/common_functions.php");    //for common function    include_once ("inc/page.inc.php");    session_start();    if (($_SESSION['mode'] == "") or ($_SESSION['FRONTEND_USER'] == "")) {        header("Location:index.php");    }    $func = new COMMONFUNC;    $db = new DB;    $flag = 0;    if ($_POST['isSubmit'] == 'save') {        $whereClause = "fldUserName='" . $_SESSION['FRONTEND_USER'] . "' and fldPassword ='" . $func -> input_fun($_POST['fldPassword']) . "'";        $error_msg = "";        if ($db -> MatchingRec(TBL_COLLEGE_COACH_REGISTER, $whereClause) == 0) {            $error_msg = 'Old Password incorrect, please re-try';        }        if ($error_msg == '') {            $strDataArr = array('fldPassword ' => $func -> input_fun($_POST['fldNewPassword']));            $where = "fldUserName='" . $_SESSION['FRONTEND_USER'] . "'";            $db -> updateRec(TBL_COLLEGE_COACH_REGISTER, $strDataArr, $where);            header("Location: CollegeChangePassword.php?msg=Password successfully updated.");        }    }?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml">	<head>		<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />		<title>My Account</title>		<META NAME="Keywords" CONTENT="My Account">		<META NAME="Description" CONTENT="My Account">		<link href="css/style.css" rel="stylesheet" type="text/css" />		<script language="Javascript" src="javascript/functions.js"></script>		<script language="JavaScript" type="text/JavaScript">            function validate() {                var error_msg = "";                if(trimString(document.frmAthReg.fldPassword.value) == "") {                    error_msg += "Please Enter Old password! \n";                }                if(trimString(document.frmAthReg.fldNewPassword.value) == "") {                    error_msg += "Please Enter New Password! \n";                }                if(trimString(document.frmAthReg.fldConfirmPassword.value) == "") {                    error_msg += "Please Enter Confirm Password! \n";                }                if(document.frmAthReg.fldNewPassword.value != document.frmAthReg.fldConfirmPassword.value) {                    error_msg += "Your Confirm Password Does not Match! \n";                }                if(error_msg != '') {                    alert(error_msg);                    return false;                } else {                    return true;                }            }		</script>	</head>	<body>		<?php            include ('header.php');		?>		<!--middle panel starts from here -->		<!--content panel starts from here -->		<div class="container">			<div class="innerWraper">				<div class="middle-bg">					<div class="cantener">						<div class="register-main">							<div class="registerPage">								<h1>Change Password</h1>																<?php                                if ($error_msg!=''){								?>								<div class="errormessage"><?php  echo $error_msg;?></div>								<?                                    }                                    if ($_REQUEST['msg']){								?>								    <div class="thankyoumessage"> <?php  echo $_REQUEST['msg'];?></font></div>																<?                                    }								?>																<div class="msg">									<font color="#0000ff">*</font> Fields are Mandatory.								</div>								<p></p>								<form name="frmAthReg" action="" method="post" enctype="multipart/form-data" onsubmit="return validate()">									<p>										<label>Old Password:</label>										<span><input type="password" name="fldPassword" ) this.value='abc@abc.com';" />											</span><font color="#0000ff">*</font>											</p>											<p>											<label>New Password:</label>											<span><input type="password" name="fldNewPassword" /><font color="#0000ff"