<?phpinclude_once ("inc/common_functions.php");include_once ("inc/page.inc.php");session_start();if (($_SESSION['mode'] == "") or ($_SESSION['FRONTEND_USER'] == "")) {    header("Location:index.php");}$func = new COMMONFUNC;$db = new DB;$flag = 0;// gets account information for the current user$query = "SELECT * FROM " . TBL_COLLEGE_COACH_REGISTER .          " WHERE fldUserName='" . $_SESSION['FRONTEND_USER'] . "'";$db->query($query);$db->next_record();$fldId = $db->f('fldId');// gets payment history for the user current$query = 'SELECT * FROM ' . TBL_PAYMENT_HISTORY . ' WHERE fldCoachId=' . $fldId;$db->query($query);$histLength = $db->num_rows();// builds human readable array of subscriptions for the current user$payments = array();while ($db->next_record()) {    $payment = array();        // gets fields from the payment history table    $payment['fldId'] = $db->f('fldId');    $payment['fldSubId'] = $db->f('fldSubId');    $payment['fldDate'] = $db->f('fldDate');    $payment['fldAmount'] = $db->f('fldAmount');    $payment['fldTransactionId'] = $db->f('fldTransactionId');        // gets the name of the sport for this subscription    $query = 'SELECT fldSportsname FROM ' . TBL_SPORTS . ' INNER JOIN ' .              TBL_COLLEGE_SUBSCRIPTION . ' ON ' . TBL_SPORTS . '.fldId=' .              TBL_COLLEGE_SUBSCRIPTION . '.fldSport WHERE ' .              TBL_COLLEGE_SUBSCRIPTION . '.fldId=' . $payment['fldSubId'];        $db1->query($query);    $db1->next_record();        $payment['fldSport'] = $db1->f('fldSportsname');        // gets the name of the subscription type    $query = 'SELECT fldName FROM ' . TBL_SUBSCRIPTION . ' INNER JOIN ' .              TBL_COLLEGE_SUBSCRIPTION . ' ON ' .              TBL_SUBSCRIPTION . '.fldId=' .              TBL_COLLEGE_SUBSCRIPTION . '.fldType WHERE ' .              TBL_COLLEGE_SUBSCRIPTION . '.fldId=' . $payment['fldSubId'];        $db1->query($query);    $db1->next_record();        $payment['fldType'] = $db1->f('fldName');        $payments[] = $payment;}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml">    <head>        <meta http-equiv="Content-Type" content="text/html; charset=utf-8" />        <title>College Prospect Network</title>        <link href="css/style.css" rel="stylesheet" type="text/css" />    </head>    <body>        <?php include 'header.php'; ?>        <div class="container">            <div class="innerWraper">                <div class="middle-bg">                    <div class="cantener">                        <div class="register-main">                            <div class="registerPage">                                <h1>Payment History</h1>                                <?php if ($histLength == 0): ?>                                <div class="thankyoumessage">                                    You have no Payment History on record.                                </div>                                <?php else: ?>                                <table cellspacing="2" cellpadding="5" bordercolor="#808080" border="0"                                        width="100%" class="tablePadd" style="border-collapse: collapse;"                                >                                    <tr>                                        <td width="20%" align="left" class="normalblack_12"><b>Payment Date</b></td>                                        <td width="20%" align="left" class="normalblack_12"><b>Invoice ID</b></td>                                        <td width="30%" align="left" class="normalblack_12"><b>Subscription Type</b></td>                                        <td width="30%" align="left" class="normalblack_12"><b>Sport</b></td>                                    </tr>                                    <?php foreach ($payments as $payment): ?>                                        <tr>                                            <td align="left" class="normalblack_12">                                                <?php echo $payment['fldDate']; ?>                                            </td>                                            <td align="left" class="normalblack_12">                                                <?php echo $payment['fldTransactionId']; ?>                                            </td>                                            <td align="left" class="normalblack_12">                                                <?php echo $payment['fldType']; ?>&nbsp;                                                ($<?php echo $payment['fldAmount']; ?>)                                            </td>                                            <td align="left" class="normalblack_12">                                                <?php echo $payment['fldSport']; ?>                                            </td>                                        </tr>                                    <?php endforeach; ?>                                </table>                                <?php endif; ?>                                <p style="margin-top: 30px">                                    <label>&nbsp;</label>                                    <span style="float: right;">                                        <input type="button" value="Back" onclick="history.go(-1)">                                    </span>                                </p>                            </div>                        </div>                    </div>                </div>            </div>        </div>        <?php include 'footer.php'; ?>    </body></html>