ect") {                    error_msg += "Please Select HS/AAU Team! \n";                }                                        //other selected                    if(trimString(document.frmCoachReg.fldSchool.value) == "other") {                                                                                                   //college name                            if(trimString(document.frmCoachReg.txtfldName.value) == "") {                                error_msg += "Please Enter School Name! \n";                            } else {                                if(hasSpecialCharaters(document.frmCoachReg.txtfldName.value)) {                                    error_msg += "Enter Valid School Name! \n";                                }                           }                                                                                             }                                     //end: other                                                          //School or other selected                    if(trimString(document.frmCoachReg.fldSchool.value) != "select") {                                                    //address                            if(trimString(document.frmCoachReg.fldAddress.value) == "") {                                error_msg += "Please Enter School Address! \n";                            } else {                                if(hasSpecialCharaters(document.frmCoachReg.fldAddress.value)) {                                    error_msg += "Enter Valid School Address! \n";                                }                           }                                                       //city                            if(trimString(document.frmCoachReg.fldCity.value) == "") {                                error_msg += "Please Enter School City! \n";                            } else {                                if(hasSpecialCharaters(document.frmCoachReg.fldCity.value)) {               