request, please use Firefox & retry:\n" + req.statusText);                            }                        }                                   }                               req.open("GET", strURL, true);                    req.send(null);                }                   }            function OLDFUNCAddschool(str) {                if(str == "") {                    document.getElementById("AjaxResponse").innerHTML = "";                    return;                }                               xmlhttp=GetXmlHttpObject();                               xmlhttp.onreadystatechange = function() {                    if(xmlhttp.readyState == 4) {                        if (xmlhttp.status == 200) {                            document.getElementById("AjaxResponse").innerHTML = xmlhttp.responseText;                        }                                            }                };                                if(str == "others") {                    document.getElementById("txtschoolothers").style.display = "";                } else if(str == "") {                    document.getElementById("txtschoolothers").style.display = "none";                    xmlhttp.open("POST", "addschool-new.php?q=" + str, true);                    xmlhttp.send();                } else {                    document.getElementById("txtschoolothers").style.display = "none";                    xmlhttp.open("POST", "addschool-new.php?q=" + str, true);                    xmlhttp.send();                }            }		</script>	</head>	<body>		<!--header link starts from here -->		<?php            include ('header.php');		?>		<!--Header ends from here -->		<!--middle panel starts from here -->		<!--content panel starts from here -->		<div class="container">			<div class="innerWraper">				<div class="middle-bg">					<div class="cantener">						<div class="register-main">							<h1>High School / AAU Coach Registration</h1>							<?phpif(