<?php    session_start();    include_once ("inc/common_functions.php");    //for common function    include_once ("inc/page.inc.php");    //for paging    include_once ("inc/config.inc.php");    $func = new COMMONFUNC;    $db = new DB;    if ($_POST['isSubmit'] == 'save') {        if ($_REQUEST['fldUsername'] == '') {            $error_msg[] = " Please Enter User Name";        }        if ($_REQUEST['fldPassword'] == '') {            $error_msg[] = "Please enter Password";        }              if ($_REQUEST['logininfo'] == "athlete")        {        ########### ATHLETE #############                       $fldUsername = $func -> input_fun($_POST['fldUsername']);            $fldPassword = $func -> input_fun($_POST['fldPassword']);            $whereClause = "fldUsername='" . $fldUsername . "' and fldPassword= '" . $fldPassword . "' ";            $retVal = $db -> MatchingRec(TBL_ATHELETE_REGISTER, $whereClause);            //If Username & Password Matches            if ($retVal != 0) {                $query = " Select fldUsername,fldEmail,fldId,fldApproveCoachId,fldStatus from " . TBL_ATHELETE_REGISTER . " where  fldUsername = '$fldUsername' ";                $db -> query($query);                $db -> next_record();                $fldUsername = $db -> f('fldUsername');                $Email = $db -> f('fldEmail');                $fldId = $db -> f('fldId');                //IsApproveds                $sessIsApproved = 0;                $fldApproveCoachId = $db -> f('fldApproveCoachId ');                if ($fldApproveCoachId != 0) {                    $sessIsApproved = 1;                }                //Status                $sessStatus= "Active";                $fldStatus = $db -> f('fldStatus');                if ($fldStatus == "DEACTIVE") {                    $sessStatus = "Inactive";                }                                if ($sessStatus == "Active")                {                    //Athlete Active - LOG THEM IN                    $_SESSION['FRONTEND_USER'] = $fldUsername;                    $_SESSION['EMAIL'] = $Email;                    $_SESSION['Athlete_id'] = $fldId;                                   $_SESSION['mode'] = "athlete";                    $_SESSION['IsApproved'] = $sessIsApproved;                    $_SESSION['IsStatus'] = $sessStatus;                    //Redirect User                    header("Location:myaccount.php");                                    }                else {
                    //Athlete Pending Approval - Display Message                   $msg_warn = "Sorry, your Application is still Pending Approval.  You will receive an Email when a decision has been made.";                            }                //End Test Active State            } else {                if ($_POST['logininfo']) {                    $msg_no = "Incorrect Username/Password. Please try again and select the correct User Type.";                } else {                    $msg_no = "Please choose User Type";                }            }        } else if ($_REQUEST['logininfo'] == "coach")//for HS Coach        {                    ########### HS COACH #############                    $fldUsername = $func -> input_fun($_POST['fldUsername']);            $fldPassword = $func -> input_fun($_POST['fldPassword']);            $whereClause = "fldUsername='" . $fldUsername . "' and fldPassword= '" . $fldPassword . "'";            $retVal = $db -> MatchingRec(TBL_HS_AAU_COACH, $whereClause);            if ($retVal != 0) {                $query = " Select fldId,fldUsername,fldEmail from " . TBL_HS_AAU_COACH . " where  fldUsername = '$fldUsername' ";                $db -> query($query);                $db -> next_record();                $fldUsername = $db -> f('fldUsername');                $Email = $db -> f('fldEmail');                $fldId = $db -> f('fldId');                //LOG THEM IN                $_SESSION['FRONTEND_USER'] = $fldUsername;                $_SESSION['EMAIL'] = $Email;                $_SESSION['Coach_id'] = $fldId;                $_SESSION['mode'] = 'coach';                                //Redirect User                header("Location:myaccount.php");            } else {                $msg_no = "Incorrect Username/Password. Please try again and select the correct User Type.";            }        } else if ($_REQUEST['logininfo'] == "school")        {                     ########### school (not used) #############                      $fldUsername = $func -> input_fun($_POST['fldUsername']);            $fldPassword = $func -> input_fun($_POST['fldPassword']);            $whereClause = "fldUserName='" . $fldUsername . "' and fldPassword= '" . $fldPassword . "'";            $retVal = $db -> MatchingRec(TBL_HS_AAU_TEAM, $whereClause);            if ($retVal != 0) {                $query = " Select fldUserName from " . TBL_HS_AAU_TEAM . " where  fldUserName = '$fldUsername' ";                $db -> query($query);                $db -> next_record();                $fldSchoolcode = $db -> f('fldUserName');                //LOG THEM IN                $_SESSION['FRONTEND_USER'] = $fldSchoolcode;                $_SESSION['mode'] = 'school';                //Redirect User                header("Location:myaccount.php");            } else {                $msg_no = "Incorrect Username/Password. Please try again and select the correct User Type.";            }        } else if ($_REQUEST['logininfo'] == "college")        {                      ########### COLLEGE COACH #############                   $fldUsername = $func -> input_fun($_POST['fldUsername']);            $fldPassword = $func -> input_fun($_POST['fldPassword']);            $whereClause = "fldUserName='" . $fldUsername . "' and fldPassword= '" . $fldPassword . "' and fldStatus ='ACTIVE'";            $retVal = $db -> MatchingRec(TBL_COLLEGE_COACH_REGISTER, $whereClause);            if ($retVal != 0) {                $query = " Select * from " . TBL_COLLEGE_COACH_REGISTER . " where  fldUserName = '$fldUsername' ";                $db -> query($query);                $db -> next_record();                $fldUserName = $db -> f('fldUserName');                $Email = $db -> f('fldEmail');                $fldId = $db -> f('fldId');                //LOG THEM IN                $_SESSION['FRONTEND_USER'] = $fldUserName;                $_SESSION['EMAIL'] = $Email;                $_SESSION['College_Coach_id'] = $fldId;                $_SESSION['mode'] = 'college';                $_SESSION['fldSubscribe'] = $db -> f('fldSubscribe');                //Redirect User                header("Location:myaccount.php");            } else {                $msg_no = "Incorrect Username/Password. Please try again and select the correct User Type.";            }        } else {            if ($_POST['logininfo']) {                $msg_no = "Incorrect Username/Password. Please try again and select the correct User Type.";            } else {                $msg_no = "Please choose User Type";            }        }    }?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml">	<head>		<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />		<title>College Prospect Network</title>		<link href="css/style.css" rel="stylesheet" type="text/css" />		<script language="Javascript" src="javascript/functions.js"></script>		<script language="JavaScript" type="text/JavaScript">            function validate(docForm) {                if(trimString(docForm.fldUsername.value) == "") {                    alert("Please enter user name.");                    docForm.fldUsername.focus();                    return false;                }                if(trimString(docForm.fldUsername.value) != "") {                    if(hasSpecialCharaters(docForm.fldUsername.value)) {                        alert("Please enter valid user name!");                        docForm.username.focus();                        return false;                    }                }                if(trimString(docForm.fldPassword.value) == "") {                    alert("Please enter password.");                    docForm.fldPassword.focus();                    return false;                }                if(docForm.logininfo.checked) {                    alert("Please Choose user type.");                    docForm.fldPassword.focus();                    return false;                }                return true;            }		</script>	</head>	<body>		<?php            include ('header.php');		?>		<!--header link starts from here -->		<!--Header ends from here -->		<!--middle panel starts from here -->		<!--content panel starts from here -->		<div class="container">			<div class="innerWraper">				<div class="middle-bg">					<div class="cantener">						<div class="register-main">							<div class="registerPage">								<form name="frmLogin" action="" method="post" enctype="multipart/form-data" onsubmit="return validate(document.frmLogin);">									<h1>Login</h1>									<?                                    if($msg_no!="")                                    {									?>									<div class="errormessage"><?  echo $msg_no;?></div>														<?                                        }                                        else if ($msg_warn!="")                                        {									?>									<div class="thankyoumessage"><?  echo $msg_warn;?></div>        																<?                                        } else if ($_REQUEST['msgforpass']!='')                                        {                                    ?>                                    <div class="thankyoumessage">                                        <?php  echo $_REQUEST['msgforpass'];?>                                    </div>                                                                  <?                                        }                                    ?>									<p>										<label>User Name:</label>										<span>											<input type="text" name="fldUsername" value="<?=$fldUsername?>" />										</span><font color="#0000ff">&nbsp;*</font>									</p>									<p>										<label>Password:</label>										<span>											<input type="password" name="fldPassword" value="<?=$fldPassword?>" />										</span><font color="#0000ff">&nbsp;*</font>									</p>									<p>										<label>User Type:</label>										<span class="rdio-spac">											<input type="radio"  name="logininfo" value="athlete" checked="checked"/>											<label>Athlete</label>											<input type="radio"  name="logininfo" value="coach">											<label>High School / AAU Coach</label>											<input type="radio"  name="logininfo" value="college"/>											<label>College Coach</label> </span>									</p>									<p>										<label>&nbsp;</label>										<span>											<input type="hidden" name="isSubmit" value="save">											<input type="submit" name="submit" value="Login"/>										</span>									</p>									<p style="margin:auto; padding:15px 0 0 264px;">										<a href="Athforgetpassword.php" style="color:#086AA8;">Forgot Username or Password?</a>									</p>								</form>							</div>						</div>					</div>				</div>			</div>		</div>		<?php            include ('footer.php');		?>	</body></html>